import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { Button, TextField, Typography, Grid } from '@mui/material';  // Import Material-UI components
import { addEmployee } from '../actions/employeeActions';

const EmployeeForm = () => {
  const dispatch = useDispatch();
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');

  const handleSubmit = () => {
    dispatch(addEmployee({ firstName, lastName }));
    // Add additional logic as needed for form submission
  };

  return (
    <Grid container spacing={2}> {/* Material-UI Grid container */}
      <Grid item xs={12}>
        <Typography variant="h5">Add Employee</Typography>
        <form>
          <TextField
            label="First Name"
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
          />
          <br />
          <TextField
            label="Last Name"
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
          />
          <br />
          <Button variant="contained" color="primary" onClick={handleSubmit}>
            Add Employee
          </Button>
        </form>
      </Grid>
    </Grid>
  );
};

export default EmployeeForm;
